import React, { useState } from 'react';
import { Link } from 'react-router-dom'
import Axios from 'axios'


function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Verificar se os campos estão preenchidos
    if (!email || !password) {
      console.log('Por favor, preencha todos os campos.');
      return;
    }

    // Fazer a requisição GET para verificar as credenciais
    Axios.get('http://localhost:3001/usuarios', {
      params: {
        email: email,
        password: password
      }
    })
      .then(response => {
        // Verificar a resposta do servidor
        if (response.data.authenticated) {
          console.log('Usuário autenticado com sucesso.');
          // Aqui você pode redirecionar o usuário para outra página
          // Por exemplo: history.push('/dashboard');
        } else {
          console.log('Email ou senha inválidos.');
        }
      })
      .catch(error => {
        console.error('Ocorreu um erro ao fazer a requisição:', error);
      });
  };

  return (
    <div className='login-container'>
      <div className='login-box'>
        <h2>LOGIN</h2>
        <form onSubmit={handleSubmit}>
          <div className='input-field'>
            <input type="email" placeholder='E-mail' value={email} onChange={handleEmailChange} />
          </div>
          <div className='input-field'>
            <input type="password" placeholder='Senha' value={password} onChange={handlePasswordChange} />
          </div>
          <div className='login-cadastro'>
            <p>Não possui uma conta? <Link to="/">Cadastre-se</Link></p>
          </div>
          <button type="submit">Entrar</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
